    private void jBtsumaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtsumaActionPerformed
        // TODO add your handling code here:
        double num1 = Double.parseDouble(txtNum1.getText());
        double num2 = Double.parseDouble(txtNum2.getText());
        
        textresultado.setText(String.valueOf(num1 + num2));
    }//GEN-LAST:event_jBtsumaActionPerformed

    private void jBtRestaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtRestaActionPerformed
        // TODO add your handling code here:
        double num1 = Double.parseDouble(txtNum1.getText());
        double num2 = Double.parseDouble(txtNum2.getText());
        
        textresultado.setText(String.valueOf(num1 - num2));
    }//GEN-LAST:event_jBtRestaActionPerformed

    private void jBtdivisionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtdivisionActionPerformed
        // TODO add your handling code here:
        double num1 = Double.parseDouble(txtNum1.getText());
        double num2 = Double.parseDouble(txtNum2.getText());
        
        textresultado.setText(String.valueOf(num1 / num2));  
    }//GEN-LAST:event_jBtdivisionActionPerformed

