    private void jBtraiz3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtraiz3ActionPerformed
        // TODO add your handling code here:
        double num1 = Double.parseDouble(txtNum1.getText());
        double raiz3;
        raiz3 = Math.cbrt(num1);
        textresultado.setText(String.valueOf(raiz3));
    }//GEN-LAST:event_jBtraiz3ActionPerformed

    private void jBtraiz2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtraiz2ActionPerformed
        // TODO add your handling code here:
        double num1 = Double.parseDouble(txtNum1.getText());
        double raiz;
        raiz = Math.sqrt(num1);
        textresultado.setText(String.valueOf(raiz));
    }//GEN-LAST:event_jBtraiz2ActionPerformed

